<span class="line"><span style="color:#F8F8F2">	w </span><span style="color:#FF79C6">:=</span><span style="color:#F8F8F2"> httptest.</span><span style="color:#50FA7B">NewRecorder</span><span style="color:#F8F8F2">()</span></span>
